age: url('img/button_open_darcula.svg?fill=%23f68322&selector=.data-fill');\n  }\n}\n\n._unfolded {\n  .fold-button {\n    background-image: url('img/button_close.svg?fill=%23d4d4d4&selector=.data-fill');\n    &._hover {\n      background-image: url('img/button_close.svg?fill=%23f68322&selector=.data-fill');\n    }\n  }\n  .fold-button.darcula {\n    background: url('img/button_close_darcula.svg') no-repeat;\n    &._hover {\n      background-image: url('img/button_close_darcula.svg?fill=%23f68322&selector=.data-fill');\n    }\n  }\n}\n\n.code-area {\n  position: relative;\n}\n\n.compiler-info {\n  &, & a {\n    font-size: 10px;\n    color: $wt-color-silver;\n  }\n\n  display: flex;\n  justify-content: flex-end;\n\n  padding-top: 5px;\n\n  position: absolute;\n  right: 0;\n  left: 0;\n\n  span {\n    margin-left: 15px;\n  }\n\n  &_crosslink {\n    justify-content: space-between;\n  }\n\n  &__open-editor {\n    flex: 1 1 0;\n  }\n\n  &__target, &__version {\n    white-space: nowrap;\n  }\n}\n\n.CodeMirror-hints {\n  padding-left: 0 !important;\n  border: 1px solid $wt-color-silver;\n  border-radius: 4px;\n  list-style: none;\n  position: absolute;\n  background-color: #f7f7f7;\n  overflow-y: hidden;\n  z-index: 10;\n  max-height: 20em;\n  box-shadow: 2px 3px 5px rgba(0, 0, 0, .2);\n}\n\n.CodeMirror-hint {\n  cursor: pointer;\n  display: flex;\n  align-items: center;\n  padding-right: 5px;\n  padding-left: 5px;\n}\n\nli.CodeMirror-hint-active {\n  background-color: #d8d8d8;\n  color: #000000;\n}\n\nli.CodeMirror-hint-active .name {\n  overflow: auto;\n  white-space: normal;\n}\n\n.CodeMirror-hint .name {\n  margin-right: 20px;\n  max-width: 60ch;\n  overflow: hidden;\n  white-space: nowrap;\n  text-overflow: ellipsis;\n}\n\nli.CodeMirror-hint-active .tail {\n  overflow: auto;\n  white-space: normal;\n}\n\n.CodeMirror-hint .tail {\n  margin-left: auto;\n  margin-right: 5px;\n  max-width: 30ch;\n  overflow: hidden;\n  white-space: nowrap;\n  text-overflow: ellipsis;\n}\n