import { useState, useEffect } from 'react';
import Container from '@mui/material/Container';

const Editor = ({ value }) => {
  const [openedEditor, setOpenedEditor] = useState('js');
  //   const [html, setHtml] = useState('');
  const [css, setCss] = useState('');
  const [js, setJs] = useState('js');
  const [srcDoc, setSrcDoc] = useState(` `);

  let html = `<div class="right-box" id ="game">
  <h2>Game View</h2>
  <canvas class="game-canvas" id="gameCanvas"></canvas>
</div>`;
  // <style>${css}</style>

  useEffect(() => {
    const timeOut = setTimeout(() => {
      setSrcDoc(
        `
          <html>
            <body>${html}</body>
            <style>
                /* Style for the right box (70% width) */
                .right-box {
                    width: 70%;
                    background-color: #d0d0d0;
                    padding: 20px;
                    box-sizing: border-box; /* Include padding in the width */
                }

                /* Style for the game view */
                .game-canvas {
                    width: 100%;
                    height: 80%;
                    background-color: black;
                }
            </style>
            <script>${value}</script>
          </html>
        `,
      );
    }, 250);
    return () => clearTimeout(timeOut);
  }, [html, js, value]);

  return (
    <iframe
      srcDoc={srcDoc}
      title="output"
      sandbox="allow-scripts"
      border="solid"
      width="100%"
      //   height="300px"
    />
  );
};

export default Editor;
